package hard;

import org.example.hard.CheckIfDigitsAreEqualInStringAfterOperationsII;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

public class CheckIfDigitsAreEqualInStringAfterOperationsIITest {

    @Test
    public void test3902() {
        final CheckIfDigitsAreEqualInStringAfterOperationsII solution = new CheckIfDigitsAreEqualInStringAfterOperationsII();
        Assertions.assertTrue(solution.hasSameDigits("3902"));
    }

    @Test
    public void test34789() {
        final CheckIfDigitsAreEqualInStringAfterOperationsII solution = new CheckIfDigitsAreEqualInStringAfterOperationsII();
        Assertions.assertFalse(solution.hasSameDigits("34789"));
    }

    @Test
    public void test44472() {
        final CheckIfDigitsAreEqualInStringAfterOperationsII solution = new CheckIfDigitsAreEqualInStringAfterOperationsII();
        Assertions.assertFalse(solution.hasSameDigits("44472"));
    }

    @Test
    public void testBigString() {
        final CheckIfDigitsAreEqualInStringAfterOperationsII solution = new CheckIfDigitsAreEqualInStringAfterOperationsII();
        long start = System.currentTimeMillis();
        boolean condition = solution.hasSameDigits("");
        System.out.println("TIME:" + (System.currentTimeMillis() - start));
        Assertions.assertTrue(condition);
    }
}
